import React , {useEffect, useState} from "react";
import {  Link , useParams } from "react-router-dom";
import axios from "axios";
import "../styles/mainpage.css";

function Mainpage(){

    const {id} = useParams();

  const [info , setInfo]=useState({
    arr:"" , task:"" , description:"" , arrdata :[]
  });

   
  const [user , setUser]=useState({
    email:"" , pass:"" , name:""
});

  
  function Load(){
    
    axios.get("/get_post")
    .then((res)=> {
       console.log(res);
      setInfo({
        arrdata : res.data
      })
    })
    .catch((err)=>{
        console.log(err); 
      })



      axios.get("/get-specific/"+id)

        .then((res)=>{
            console.log(res);
            setUser(res.data);

        })
        .catch((err)=>{
            console.log(err);
        })
}

    useEffect(()=>{
      Load();
    }, []
  )


   
    



   

    function UpdateData(user_id , post_id ,id){
          

        axios.put("/update/"+user_id+"/"+post_id+"/"+id, info)

        .then((res)=>{
            console.log(res);
           
        })
        .catch((err)=>{
            console.log(err);
        })
    }




    return(



        <>
        <div className="start">

       <nav class="navbar navbar-expand-lg navbar-light bg-light">
        <div class="container-fluid">
            <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarTogglerDemo01" aria-controls="navbarTogglerDemo01" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse" id="navbarTogglerDemo01">
            <a class="navbar-brand" href="#"> Welcome {user.email}</a>
           
            <Link to={"/User_Profile/"+id}>
                <button  className="btn btn-primary" id="update-but">Update Profile</button>
            </Link>
            
         
            </div>
        </div>
        </nav>


           
            
                
               
            
        </div>

        <div>
      

    <nav>
      <a href="#">Home</a>
      <a href="#">About</a>
      <a href="#">Blog</a>
      <a href="#">Portfolio</a>
      <a href="#">Contact</a>
      <div class="animation start-home"></div>
    </nav>


 
    
    {info.arrdata.map((post , pos)=>{
    return(
    <div>
        

        <div>
        {post.card.map((sub , so)=>{
            return(
        <div className="card">
                    
                    
                    <b className="card-header">{post.name}</b>
                    
                    <div className="card-body">
                        
                    <b className="card-header">{sub.task}</b>
                    
                    <button  onClick={()=>{UpdateData(post._id , sub._id , id)}} className="btn btn-primary">Task Completed</button>
                    </div>
                    
                                
        </div>

            )

        })}
        </div>

    </div>
    )
    })
    }



 
    </div>
      
        </>
    )

}
export default Mainpage;
