import React , {useEffect, useState} from "react";
import {  Link , useParams } from "react-router-dom";
import axios from "axios";
import "../styles/Actual_Quiz.css";

function Actual_Quiz(){

        const {id} = useParams();
        const {Quiz_id} = useParams();
        const {user_id} = useParams();

  const [info , setInfo]=useState({
     Title_Quiz:"" , selected_option:"" , arrdata :[]
  });

  const [data , setData]=useState({
    Title_Quiz:"" , selected_option:"" ,que:"" , option1:"", option2:"" , option3:"" , option4:"", ans:"" ,  arrdata :[]
 });

   
  const [user , setUser]=useState({
    email:"" , pass:"" , name:""
});

  

const handleChange = event =>{

    const target = event.target
    const name = target.name
    const value = target.value
  
    setData({
      ...data , [name] : value
    })
   }
   


  function Load(){
    
    axios.get("/get-specific-quiz/"+id+"/"+Quiz_id)
    .then((res)=> {
       console.log(res);
      setInfo({
        arrdata : res.data.Quiz
      })
    })
    .catch((err)=>{
        console.log(err); 
      })



      axios.get("/get_specific_user/"+id)

        .then((res)=>{
            console.log(res);
            setUser(res.data);

        })
        .catch((err)=>{
            console.log(err);
        })
}

    useEffect(()=>{
      Load();
    }, []
  )





   

    function UpdateData(){
          

        axios.put("/sub_que/"+user_id, data)

        .then((res)=>{
            console.log(res);
           
        })
        .catch((err)=>{
            console.log(err);
        })
    }




    return(



        <>
        
        <div>
      


	<nav class="navbar navbar-expand-lg navbar-light fixed-top">
		<div class="container">
			<a class="navbar-brand" href="#"><b> Welcome</b></a> <button aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation" class="navbar-toggler" data-target="#navbarSupportedContent" data-toggle="collapse" type="button"><span class="navbar-toggler-icon"></span></button>
			<div class="collapse navbar-collapse" id="navbarSupportedContent">
                
				<ul class="navbar-nav ml-auto">
					
					<li class="nav-item">
						<a class="nav-link" href="#">
                            <Link to={"/take_quiz/"+id}>
                                 <button  className="header btn btn-primary"> Take Quiz</button>
                            </Link>            
                        </a>
					</li>
					<li class="nav-item">
						<a class="nav-link" href="#">
                            <Link to={"/AllPost/"+id}>
                                <button  className="header btn btn-primary">View Created Quizs</button>
                            </Link>
                        </a>
					</li>
					
                    <li>
                    <Link to={"/User_Profile/"+id}>
                            <button  className="btn btn-primary" id="update-but">Update Profile</button>
                    </Link>
            
                    </li>
				</ul>
			</div>
		</div>
	</nav>
<div className="Content">
    <div class="card border-dark mb-3" >

    {info.arrdata.map((post , pos)=>{
    return(
    <div>
        
        
    <div class="card-header"> <b>Title Of Quiz is : </b>{post.Title_quiz}</div>

  
        {post.Questions.map((sub , so)=>{
            return(
            <>

           
            <div class="card border-dark mb-3 question">
            <div class="card-header"><b>Question : </b>{sub.que}</div>

            <div class="card-body text-dark">
                <form>
                    <p class="card-text"><input type="radio" name="selected_option" value={sub.option1} onChange={handleChange} />(A) : <strong>{sub.option1}</strong></p>
                    <p class="card-text"><input type="radio" name="selected_option" value={sub.option2} onChange={handleChange} />(B) : <strong>{sub.option2}</strong></p>
                    <p class="card-text"><input type="radio" name="selected_option" value={sub.option3} onChange={handleChange} />(C) : <strong>{sub.option3}</strong></p>
                    <p class="card-text"><input type="radio" name="selected_option" value={sub.option4} onChange={handleChange} />(D) : <strong>{sub.option4}</strong></p>
                </form>
                
                {data.Title_quiz = post.Title_quiz};
                {data.que = sub.que};
                {data.ans = sub.ans};
                {data.option1 = sub.option1};
                {data.option2 = sub.option2};
                {data.option3 = sub.option3};
                {data.option4 = sub.option4};
                    <br/>
                <b>Option : {data.ans}</b>
                {data.selected_option = info.selected_option};
                <br/>
                <button  onClick={()=>{UpdateData()}} className="btn btn-primary">Submit</button>
                <h5 class="card-title"><b>Answer : </b>{sub.ans}</h5>
            </div>
            </div>
            
                    
             </>

            )
       
       })}
       

    </div>
    )
    })}


    </div> 
  </div>
</div>
      

        </>
    )

}
export default Actual_Quiz;
