import React , {useEffect, useState} from "react";
import {  Link , useParams } from "react-router-dom";
import axios from "axios";
import "../styles/Actual_Quiz.css";

function View_Score(){

        const {id} = useParams();
        const {title} = useParams();
        

       

 const [score , setScore]=useState({
  Score_Quiz:"" , arrdata:[]
});



 function Load(){
    
   
    axios.get("/score_quiz/"+id+"/"+title)
    .then((res)=> {
       console.log(res.data);
      setScore({ 
        arrdata : res.data
      })
     
  
    })
    .catch((err)=>{
        console.log(err); 
      })
    }
  
  
  useEffect(()=>{
    Load();
  }, []
)








return(



  <>
  
  <div>



<nav class="navbar navbar-expand-lg navbar-light fixed-top">
<div class="container">
<a class="navbar-brand" href="#"><b> Welcome</b></a> <button aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation" class="navbar-toggler" data-target="#navbarSupportedContent" data-toggle="collapse" type="button"><span class="navbar-toggler-icon"></span></button>
<div class="collapse navbar-collapse" id="navbarSupportedContent">
          
  <ul class="navbar-nav ml-auto">
    
    <li class="nav-item">
      <a class="nav-link" href="#">
                      <Link to={"/take_quiz/"+id}>
                           <button  className="header btn btn-primary"> Take Quiz</button>
                      </Link>            
                  </a>
    </li>
    <li class="nav-item">
      <a class="nav-link" href="#">
                      <Link to={"/AllPost/"+id}>
                          <button  className="header btn btn-primary">View Created Quizs</button>
                      </Link>
                  </a>
    </li>
    
              <li>
              <Link to={"/User_Profile/"+id}>
                      <button  className="btn btn-primary" id="update-but">Update Profile</button>
              </Link>
      
              </li>
  </ul>
</div>
</div>
</nav>
<div className="Content">
<div class="card border-dark mb-3" >


<div>
  
  
<div class="card-header"> <b>Title Of Quiz is : </b>{score.arrdata.Quiz.Title_quiz}</div>


  {score.arrdata.Questions.map((sub)=>{
      return(
      <div key={sub._id}>

       
     
      <div class="card border-dark mb-3 question">
      <div class="card-header"><b>Question : </b>{sub.que}</div>

      <div class="card-body text-dark">
       
          <form>
              <p class="card-text"><input type="radio" name="selected_option" value={sub.option1}   />(A) : <strong>{sub.option1}</strong></p>
              <p class="card-text"><input type="radio" name="selected_option" value={sub.option2}  />(B) : <strong>{sub.option2}</strong></p>
              <p class="card-text"><input type="radio" name="selected_option" value={sub.option3}  />(C) : <strong>{sub.option3}</strong></p>
              <p class="card-text"><input type="radio" name="selected_option" value={sub.option4}  />(D) : <strong>{sub.option4}</strong></p>
          </form>
          
          
              <br/>
         
          
          <br/>
          <h5 class="card-title"><b>Answer : </b>{sub.ans}</h5>
      </div>
      </div>
      
              
       </div>

      )
 
 })}
 

<b>Score : {score.Score_Quiz}</b>
</div>




</div> 
</div>
</div>


  </>
)

}
export default View_Score;
