import React , {useEffect, useState} from "react";
import {  Link , useParams } from "react-router-dom";
import axios from "axios";
import "../styles/mainpage.css";


  import { GiHamburgerMenu } from "react-icons/gi";
  
  import { NavLink } from "react-router-dom";


function Mainpage(){

    const [showMediaIcons, setShowMediaIcons] = useState(false);

    const {id} = useParams();

  const [info , setInfo]=useState({
    arr:"" , task:"" , description:"" , arrdata :[]
  });

   
  const [user , setUser]=useState({
    email:"" , pass:"" , name:""
});

  
  function Load(){
    
    axios.get("/get_post")
    .then((res)=> {
       console.log(res);
      setInfo({
        arrdata : res.data
      })
    })
    .catch((err)=>{
        console.log(err); 
      })



      axios.get("/get-specific/"+id)

        .then((res)=>{
            console.log(res);
            setUser(res.data);

        })
        .catch((err)=>{
            console.log(err);
        })
}

    useEffect(()=>{
      Load();
    }, []
  )


   
    



   

    function UpdateData(user_id , post_id ,id){
          

        axios.put("/update/"+user_id+"/"+post_id+"/"+id, info)

        .then((res)=>{
            console.log(res);
           
        })
        .catch((err)=>{
            console.log(err);
        })
    }




    return(



        <>
        <div className="start">


        
    <>
      <nav className="main-nav">
        {/* 1st logo part  */}
        <div className="logo">
          <h2>
            <span>T</span>hapa
            <span>T</span>echnical
          </h2>
        </div>

        {/* 2nd menu part  */}
        <div
          className={
            showMediaIcons ? "menu-link mobile-menu-link" : "menu-link"
          }>
          <ul>
            <li>
              <NavLink to="/">Home</NavLink>
            </li>
            <li>
              <NavLink to="/about">about</NavLink>
            </li>
            <li>
              <NavLink to="/service">services</NavLink>
            </li>
            <li>
              <NavLink to="/contact">contact</NavLink>
            </li>
          </ul>
        </div>

        {/* 3rd social media links */}
        <div className="social-media">
          <ul className="social-media-desktop">
            <li>
              <a
                href="https://www.youtube.com/channel/UCwfaAHy4zQUb2APNOGXUCCA"
                target="_thapa">
                <FaFacebookSquare className="facebook" />
              </a>
            </li>
            <li>
              <a
                href="https://www.instagram.com/thapatechnical/"
                target="_thapa">
                <FaInstagramSquare className="instagram" />
              </a>
            </li>
            <li>
              <a
                href="https://www.youtube.com/channel/UCwfaAHy4zQUb2APNOGXUCCA"
                target="_thapa">
                <FaYoutubeSquare className="youtube" />
              </a>
            </li>
          </ul>

          {/* hamburget menu start  */}
          <div className="hamburger-menu">
            <a href="#" onClick={() => setShowMediaIcons(!showMediaIcons)}>
              <GiHamburgerMenu />
            </a>
          </div>
        </div>
      </nav>

      {/* hero section  */}
      {/* <section className="hero-section">
        <p>Welcome to </p>
        <h1>Thapa Technical</h1>
      </section> */}
    </>
 

 
    
    {info.arrdata.map((post , pos)=>{
    return(
    <div>
        

        <div>
        {post.card.map((sub , so)=>{
            return(
        <div className="card">
                    
                    
                    <b className="card-header">{post.name}</b>
                    
                    <div className="card-body">
                        
                    <b className="card-header">{sub.task}</b>
                    
                    <button  onClick={()=>{UpdateData(post._id , sub._id , id)}} className="btn btn-primary">Change</button>
                    </div>
                    
                                
        </div>

            )

        })}
        </div>

    </div>
    )
    })
    }



</div> 
   
      
        </>
    )

}
export default Mainpage;
