import React , {useEffect, useState} from "react";
import {  Link , useParams } from "react-router-dom";
import axios from "axios";
import "../styles/mainpage.css";

function Mainpage(){

    
  const [info , setInfo]=useState({
    arr:"" , prr:"" , arrdata :[]
  });

  
  function Load(){
    
    axios.get("/get_post")
    .then((res)=> {
       console.log(res);
      setInfo({
        arrdata : res.data
      })
    })
    .catch((err)=>{
        console.log(err); 
      })
}

    useEffect(()=>{
      Load();
    }, []
  )


    
    const [user , setUser]=useState({
        email:"" , pass:"" , name:""
    });

    const { id }=useParams();

    function Loaddata(){
        axios.get("/get-specific/"+id)

        .then((res)=>{
            console.log(res);
            setUser(res.data);

        })
        .catch((err)=>{
            console.log(err);
        })
    }

    useEffect(()=>{
        Loaddata();
    } , []);
    
    return(



        <>
        <div className="start">

       <nav class="navbar navbar-expand-lg navbar-light bg-light">
        <div class="container-fluid">
            <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarTogglerDemo01" aria-controls="navbarTogglerDemo01" aria-expanded="false" aria-label="Toggle navigation">
            <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse" id="navbarTogglerDemo01">
            <a class="navbar-brand" href="#"> Welcome {user.email}</a>
           
            <Link to={"/User_Profile/"+id}>
                <button  className="btn btn-primary" id="update-but">Update Profile</button>
                </Link>
            
         
            </div>
        </div>
        </nav>


           
            
                
               
            
        </div>

        <div>
      
        <Link to={"/AllPost/"+id}>
        <button  className="btn btn-primary">Market</button>
        </Link>
    

   
        <Link to={"/Post/"+id}>
        <button  className="header btn btn-primary"> Create Post</button>
        </Link>
    

        <Link to={"/AllPost/"+id}>
        <button  className="header btn btn-primary">View My Products</button>
        </Link>
 

	<nav class="navbar navbar-expand-lg navbar-light fixed-top">
		<div class="container">
			<a class="navbar-brand" href="#">Creativo</a> <button aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation" class="navbar-toggler" data-target="#navbarSupportedContent" data-toggle="collapse" type="button"><span class="navbar-toggler-icon"></span></button>
			<div class="collapse navbar-collapse" id="navbarSupportedContent">
				<ul class="navbar-nav ml-auto">
					<li class="nav-item active">
						<a class="nav-link" href="#">Home</a>
					</li>
					<li class="nav-item">
						<a class="nav-link" href="#">About</a>
					</li>
					<li class="nav-item">
						<a class="nav-link" href="#">Portfolio</a>
					</li>
					<li class="nav-item">
						<a class="nav-link" href="#">Services</a>
