import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import img from "../pictures/234403.jpg"
import "../styles/Front.css";
import axios from "axios";
import React , {useEffect, useState} from "react";
import { useParams } from "react-router-dom";

function Front(){
  const { id } = useParams();
        
  const [info , setInfo]=useState({
    arr:"" , arr2:"" , arrdata :[]
  })

  const [user , setUser] = useState({
    email : "" , pass :"" ,sir_name:"" ,product:""
   });



   
  
  function UpdateData(e){
        e.preventDefault();
        axios.put("/update/"+id , user)
        .then(data=>{
          console.log(data)          
        }).catch((err)=>{
            console.log(err);
          })
      }

      function handlechange(e){
        setInfo({
          ...info , [e.target.name] : e.target.value }
        )}



      function handleinputs(e){
        setUser({...user , [e.target.name] : e.target.value });
      };
  

      

    function PostData(e){
      e.preventDefault();

      axios.put("/entry/"+id , info)
      .then(data=>{
        console.log(data)
      
      }).catch((err)=>{
          console.log(err);
        })
  }  


      

  function Loaddata(){
    axios.get("/get-specific/"+id )
    .then((res)=> {
       console.log(res);
      setInfo({
        arrdata : res.data
      })
    })
    .catch((err)=>{
        console.log(err); 
      })
}

    useEffect(()=>{
      Loaddata();
    }, []
  )



    return(
        <>
        
            
    <div className="container">
        <img src={img} className="rounded" alt="..."/>
      
          <form method="PUT" onSubmit={(e)=>UpdateData(e)}>
            <div className="centered">Welcome {user.sir_name}</div>
            <input type="text" onChange={(e)=>{handleinputs(e)} } name="sir_name"   placeholder="enter sirname "></input>
            
            <input type="submit" value="click me "></input>


        </form>
        <form method="PUT" onSubmit={(e)=>PostData(e)}>
            <input type="text" onChange={(e)=>{handlechange(e)}} name="arr1" placeholder="Enter post name "></input>
            <input type="submit" value="ccc "></input>
            </form>
            
           
    </div>

    <div>

      {info.card.arrdata.map((post , pos)=>{

        return(
          <>

          <b> {post.prr} </b>

          </>


        )

      })
      }


    </div>
    
  
        </>
        
    )

}
export default Front;
