import React , {useEffect, useState} from "react";
import {  Link , useParams } from "react-router-dom";
import axios from "axios";
import "../styles/mainpage.css";

function Mainpage(){

    
  const [info , setInfo]=useState({
    arr:"" , prr:"" , arrdata :[]
  });

  
  function Load(){
    
    axios.get("/get_post")
    .then((res)=> {
       console.log(res);
      setInfo({
        arrdata : res.data
      })
    })
    .catch((err)=>{
        console.log(err); 
      })
}

    useEffect(()=>{
      Load();
    }, []
  )


    
    const [user , setUser]=useState({
        email:"" , pass:"" , name:""
    });

    const { id }=useParams();

    function Loaddata(){
        axios.get("/get-specific/"+id)

        .then((res)=>{
            console.log(res);
            setUser(res.data);

        })
        .catch((err)=>{
            console.log(err);
        })
    }

    useEffect(()=>{
        Loaddata();
    } , []);
    
    return(



        <>
        <div className="start">
            <b>Welcome {user.email}</b>
            
            <Link to={"/User_Profile/"+id}>
            <button  className="btn btn-primary">Update Profile</button>
            </Link>

        </div>


        <div>
        <Link to={"/AllPost/"+id}>
        <button  className="btn btn-primary">Market</button>
        </Link>
        </div>

        <div>
        <Link to={"/Post/"+id}>
        <button  className="btn btn-primary"> Create Post</button>
        </Link>
        </div>
        
        
        
        <div>
        <Link to={"/AllPost/"+id}>
        <button  className="btn btn-primary">View My Products</button>
        </Link>
        </div>
        





        <div>


            
                {info.arrdata.map((post , pos)=>{

            return(

            <div>



           

            <div>
            {post.card.map((sub , so)=>{

                return(


                <div className="card">
                    <b>{post.name}</b>
                <h5 className="card-header">{sub.prr}</h5>
                <div className="card-body">
                    

                            
                    </div>
                </div>
                        





                )

            })}

            </div>



            </div>
            )
            })
            }



        </div>
        </>
    )

}
export default Mainpage;
