import {  Link , useHistory } from "react-router-dom";
import '../styles/Login.css';
import React , {useEffect, useState } from "react";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
require ("../pictures/bg.svg");
require("../pictures/avatar.svg");
require("../pictures/wave.png");

function Login(){


  const history = useHistory();
  const [user , setUser] = useState({
    arr:"" , prr:"" , email : "" , pass :"" , data:[]
 
   });

   function Loaddata(){
     axios.get("/getdata")
     .then((response)=>{
        console.log(response);
        setUser({
        data : response.data
        })
  
      })
     .catch((err)=>{
       console.log(err);
     })
   }

    useEffect(()=>{
      Loaddata();
    }, []
    )


 
   let name,value ;
   
 const handleInputs = (e) => {
   console.log(e);
   name=e.target.name;
   value=e.target.value;
 
   setUser({ ...user , [name]:value});
 }
 
 
 
 function PostData(e){
   e.preventDefault();
   
   
  axios.post("/login" , user)
   .then(function(response){
 
     var adi = response.data;
     console.log(adi._id)
     history.push("/main/"+adi._id);
     window.alert(response);
   
   
   }).catch((err)=>{
     console.log(err);
   })
   
 }
 

 return(
        <>




	<div className="container">

	<div className="login-content">
    <form method="post" onSubmit={(e)=>PostData(e)}>
			
	<input type="email" className="emaill" onChange={handleInputs} placeholder="Username" required={true} name="email" />
	<input type="password" className="pass-input" onChange={handleInputs} placeholder="Password" required={true} name="pass" />
	<input  type="submit" className="btn btn-primary" id="Aditya"/>
	
	
	       		<div className="input-div one">
           		   <div className="i">
           		   		<i className="fas fa-user"></i>
           		   </div>
           		   <div className="div">
           		   		
           		   		
           		   </div>
           		</div>
           		<div className="input-div pass">
           		   <div className="i"> 
           		    	<i className="fas fa-lock"></i>
           		   </div>
           		   <div className="div">
           		    	
           		    	<input type="password" className="pass-input" onChange={handleInputs} placeholder="Password" required={true} name="pass" />
            	   </div>
            	</div>
              <div>
            	  <a href="#">Forgot Password?</a>
              </div>
              <div>
            <input  type="submit" className="btn btn-primary" id="Aditya"/>
            </div>
            <Link to={"/register"}>
            <button  className="btn btn-primary" >Register</button>
            </Link>
           </form>
        </div>




        <div class="img">
		    	<img src="bg.svg"/>
    		</div>



    </div>
    



    </>
    
    );
}
export default Login;
