import "../styles/Countdown.css"
const CountDown = () => {
    const [timeHr,setTimeHr]=useState(1);
    const [timeMin,setTimeMin]=useState(0);
    const [timeSec,setTimeSec]=useState(0);



    let interval;

    const startTimer=()=>{

        const CountDownDate = new Date("may 25,2022").getTime();

        interval = setInterval(() => {
            const now = new Date().getTime();
            const distances = countDownDate - now;

            const hr = Math.floor(distances % (24 * 60 * 60 * 1000)/(1000 * 60 * 60));

            const min = Math.floor(distances / (1000 * 60 * 60))/(1000 * 60);

            const sec = Math.floor(distances / (1000 * 60))/(1000)

        }, interval);

    }


    return (
        <div className="countdown">

             <h1>{timeHr}:</h1>
             <h1>{timeMin}:</h1>
             <h1>{timeSec}</h1>
        </div>
    )
}

export default CountDown
