import {  Link , useHistory } from "react-router-dom";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";

import "../styles/Create_quiz.css";
import axios from "axios";
import React , {useEffect, useState} from "react";
import { useParams } from "react-router-dom";



function Create_quiz(){
  const { id } = useParams();
  const history = useHistory();   

  const [info , setInfo]=useState({
    Title_quiz:"" , Desc_Quiz:"" ,
  });

  
  const [data , setData]=useState({
    Title_quiz:"" , que:"" , ans :""
  });



   
  
  
      function handlechange(e){
        setInfo({
          ...info , [e.target.name] : e.target.value }
        )}

        


        function PostQuestion(e){
          e.preventDefault();
    
          axios.put("/question/"+id , info)
          .then(data=>{
            console.log(data)
            history.push("/main/"+id)
          
          }).catch((err)=>{
              console.log(err);
            })
      }  
      

    function PostData(e){
      e.preventDefault();

      axios.put("/entry/"+id , info)
      .then(data=>{
        console.log(data)
        history.push("/main/"+id)
      
      }).catch((err)=>{
          console.log(err);
        })
  }  

function Load(){


  
  let cartItem = document.querySelector('.cart-items-container');
  
  document.querySelector('#cart-btn').onclick = () =>{
      cartItem.classList.toggle('active');
      navbar.classList.remove('active');
      searchForm.classList.remove('active');
  }
  
}

  useEffect(()=>{
    Load();
  }, []
)


    return(
        <>



<div class="cart-items-container">
            
    <div class="content">
      
      

      <form method="post" onSubmit={(e)=>PostQuestion(e)}>
			<div className="email-div">		
				<input type="text" className="emaill" onChange={handleInputs} placeholder="Question" required={true} name="que" />
			</div>
			<div className="pass-div">
				<input type="text" className="pass-input" onChange={handleInputs} placeholder="Answer" required={true} name="ans" />
			</div>
			<div className="submit-div">
				<input  type="submit" className="btn" id="Aditya"/>
			
				
        
				
			</div>
    	</form>

    </div>



      
        
    </div>


        
	<nav class="navbar navbar-expand-lg navbar-light fixed-top">
		<div class="container">
			<a class="navbar-brand" href="#"><b> Welcome</b></a> <button aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation" class="navbar-toggler" data-target="#navbarSupportedContent" data-toggle="collapse" type="button"><span class="navbar-toggler-icon"></span></button>
			<div class="collapse navbar-collapse" id="navbarSupportedContent">
                
				<ul class="navbar-nav ml-auto">
					<li class="nav-item active">
						<a class="nav-link" href="#">
                            <Link to={"/main/"+id}>
                                <button  className="btn btn-primary">Home</button>
                            </Link>
                        </a>
					</li>
					<li class="nav-item">
						<a class="nav-link" href="#">
                          
                                 <button  className="header btn btn-primary"> Create Post</button>
                                 
                        </a>
					</li>
					<li class="nav-item">
						<a class="nav-link" href="#">
                            <Link to={"/AllPost/"+id}>
                                <button  className="header btn btn-primary">View My Products</button>
                            </Link>
                        </a>
					</li>
					
                    <li>
                    <Link to={"/User_Profile/"+id}>
                            <button  className="btn btn-primary" id="update-but">Update Profile</button>
                    </Link>
            
                    </li>
				</ul>
			</div>

      
		</div>
	</nav>

        
            
    <div className="cont">
      
      
         

         <form method="PUT" onSubmit={(e)=>PostData(e)}>
            <input type="text" onChange={(e)=>{handlechange(e)}} name="Title_quiz" required={true} placeholder="Enter Title Of Quiz "></input>
           <br />
            <textarea type="text" onChange={(e)=>{handlechange(e)}} name="Desc_Quiz" required={true} placeholder="Enter short description about Quiz "></textarea>
           <br/>
           <br/>
           
            <input type="submit" className="btn btn-primary"></input>
         </form>

        <div>
        <button id="cart-btn">Login As User</button>
        </div>
       
            
           
    </div>

    


    
    
  
        </>
        
    )

}
export default Create_quiz;
