import '../styles/App.css';
import React , {useState} from "react";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";

function App() {


  const [user , setUser] = useState({
    login : "" , pass :""

  });

  let name,value ;

const handleInputs = (e) => {
  console.log(e);
  name=e.target.name;
  value=e.target.value;

  setUser({ ...user , [name]:value});
}
function PostData(e){
  e.preventDefault();


 axios.post("/register" , user)
  .then(function(response){

    console.log(response);
    window.alert("done");
    history.push("/");

  }).catch((err)=>{
    console.log(err);
  })

}



  return (
  <>


    <div id="samarth" >
        <form  method="POST">
                    <h1>Login</h1>
                <div className="container">
                    <i className="fa fa-user" aria-hidden="true"></i>
                    <input type="text" 
                    value={user.login}
                    onChange={handleInputs}
                    placeholder="Login Id" name="login" />
            </div>
            <div className="container">
                <i className="fa fa-lock" aria-hidden="true"></i>
                <input type="password" 
                value={user.pass}
                onChange={handleInputs}  placeholder="password" name="pass" /><br />
            </div>
            <div id="third">
                <input  type="submit" value="submit"  className="btn" id="Aditya" 
                onClick={PostData} />

            </div>
       </form>
    </div>



  </>
  );
}
export default App;
