import {  Link , useParams } from "react-router-dom";
import "../styles/AllPost.css"
import React , {useEffect, useState } from "react";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";



function AllPost(){

    const {id} = useParams();
  
         
  const [info , setInfo]=useState({
    arr:"" , prr:"" ,product_status:"" , arrdata :[]
  });

  const [Sold_info , setSold_info]=useState({
    arr:"" , prr:"" ,pro_status:"" , arrdata :[]
  });

          

function Loaddata(){
    axios.get("/get-specific/"+id )
    .then((res)=> {
       console.log(res);
      setInfo({
        arrdata : res.data.card
      })
    })
    .catch((err)=>{
        console.log(err); 
      })

      axios.get("/get-specific/"+id )
      .then((res)=> {
         console.log(res);
        setSold_info({
          arrdata : res.data.sold
        })
      })
      .catch((err)=>{
          console.log(err); 
        })
}

    useEffect(()=>{
      Loaddata();
  }, []
  )


  

     
        function UpdateData(id , post_id){
          

            axios.put("/update/"+id+"/"+post_id, info)

            .then((res)=>{
                console.log(res);
               
            })
            .catch((err)=>{
                console.log(err);
            })
        }



  function deletepost(id , post_id){

    axios.delete("/delete/"+id+"/"+post_id)
    .then(data=>{
      console.log(data)
    
    }).catch((err)=>{
        console.log(err);
      })


  }




    return(
        <>

        
	<nav class="navbar navbar-expand-lg navbar-light fixed-top">
		<div class="container">
    <Link to={"/main/"+id}>
         <button  className="header btn btn-primary"> Back</button>
    </Link> 
			<a class="navbar-brand" href="#"><b> Welcome </b></a> <button aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation" class="navbar-toggler" data-target="#navbarSupportedContent" data-toggle="collapse" type="button"><span class="navbar-toggler-icon"></span></button>
			<div class="collapse navbar-collapse" id="navbarSupportedContent">
                
				<ul class="navbar-nav ml-auto">
					<li class="nav-item active">
						<a class="nav-link" href="#">
                        <Link to={"/main/"+id}>
                                <button  className="btn btn-primary">Home</button>
                        </Link>
                        </a>
					</li>
					<li class="nav-item">
						<a class="nav-link" href="#">
                            <Link to={"/Post/"+id}>
                                 <button  className="header btn btn-primary"> Create Post</button>
                            </Link>            
                        </a>
					</li>
					<li class="nav-item">
						<a class="nav-link" href="#">
                            
                                <button  className="header btn btn-primary">View My Products</button>
                         
                        </a>
					</li>
					
                    <li>
                    <Link to={"/User_Profile/"+id}>
                            <button  className="btn btn-primary" id="update-but">Update Profile</button>
                    </Link>
            
                    </li>
				</ul>
			</div>
		</div>
	</nav>





      <div className="cont">
        <div>
          <Link to={"/Post/"+id}>
          <button  className="btn btn-primary">View My current products in market</button>
          </Link>
        </div>


        <div>
          <Link to={"/Post/"+id}>
          <button  className="btn btn-primary">View My solded products</button>
          </Link>
        </div>
        
        <div>
          <Link to={"/AllPost/"+id}>
          <button  className="btn btn-primary">View I getted products</button>
          </Link>
        </div>
      
      {info.arrdata.map((post , pos)=>{
         
return(
  
 
    <div className="card">
      
      <b className="card-header">{post.prr}</b>
      <div className="card-body">
          
      <button  onClick={()=>{UpdateData(id , post._id)}} className="btn btn-primary">Change</button>    
      <button onClick={()=>{deletepost( id , post._id)}} className="btn btn-primary">Delete</button>

      </div>
      
                
    </div>
            



  


)

})
}

     
{Sold_info.arrdata.map((post , pos)=>{
         
         return(
           
          
             <div className="card">
               
               <b className="card-header">{post.prr}</b>
               <div className="card-body">
                   
                 
               <button onClick={()=>{deletepost( id , post._id)}} className="btn btn-primary">Delete</button>
         
               </div>
               
                         
             </div>
                     
         
         
         
           
         
         
         )
         
         })
         }
</div>
        </>


    )

}
export default AllPost;
