import {  Link , useHistory } from "react-router-dom";
import '../styles/Login.css';
import React , {useEffect, useState } from "react";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
require ("../pictures/bg.svg");
require("../pictures/avatar.svg");
require("../pictures/wave.png");

function Login(){

  const [info , setInfo]=useState({
    arr:"" , prr:"" , arrdata :[]
  });

  
  function Load(){
    
    axios.get("/get_post")
    .then((res)=> {
       console.log(res);
      setInfo({
        arrdata : res.data
      })
    })
    .catch((err)=>{
        console.log(err); 
      })
}

    useEffect(()=>{
      Load();
    }, []
  )



  const history = useHistory();
  const [user , setUser] = useState({
    arr:"" , prr:"" , email : "" , pass :"" , data:[]
 
   });

   function Loaddata(){
     axios.get("/getdata")
     .then((response)=>{
        console.log(response);
        setUser({
        data : response.data
        })
  
      })
     .catch((err)=>{
       console.log(err);
     })
   }

    useEffect(()=>{
      Loaddata();
    }, []
    )


 
   let name,value ;
   
 const handleInputs = (e) => {
   console.log(e);
   name=e.target.name;
   value=e.target.value;
 
   setUser({ ...user , [name]:value});
 }
 
 
 
 function PostData(e){
   e.preventDefault();
   
   
  axios.post("/login" , user)
   .then(function(response){
 
     var adi = response.data;
     console.log(adi._id)
     history.push("/main/"+adi._id);
     window.alert(response);
   
   
   }).catch((err)=>{
     console.log(err);
   })
   
 }
 
/*

    <div className="container" >



 



     <form method="post" onSubmit={(e)=>PostData(e)}>
        <div className="inputs">
        <input type="email" className="emaill" onChange={handleInputs} required={true} placeholder="enter your email" name="email" />
        </div>
        <div className="inputs">
        <input type="password" className="pass-input" onChange={handleInputs} required={true} placeholder="enter your pass" name="pass" />
        </div>
        <div>
        <input  type="submit" className="btn" id="Aditya"/>
        
        <Link to={"/register"}>
        <button  id="BUT">Register</button>
        </Link>
        </div>
      </form>
    </div>
    
  



    <div id="sam">
          
          {info.arrdata.map((post , pos)=>{

            return(
          
          <div>
          
            
          
            <b> Hello{post.email} </b>
          
            <div>
              {post.card.map((sub , so)=>{

                return(
                <b>
                <h1>{sub.prr}</h1>
                </b>
                )

              })}
          
            </div>
            
          
          
          </div>
            )
          })
          }
          
              
              
              </div>


*/

 return(
        <>



	<img class="wave" src="wave.png"/>
	<div class="container">

		<div class="login-content">
    <form method="post" onSubmit={(e)=>PostData(e)}>
				<img src="avatar.svg"/>
				<h2 class="title">Welcome</h2>
           		<div class="input-div one">
           		   <div class="i">
           		   		<i class="fas fa-user"></i>
           		   </div>
           		   <div class="div">
           		   		
           		   		<input type="email" className="emaill" onChange={handleInputs} placeholder="Username" required={true} name="email" />
           		   </div>
           		</div>
           		<div class="input-div pass">
           		   <div class="i"> 
           		    	<i class="fas fa-lock"></i>
           		   </div>
           		   <div class="div">
           		    	
           		    	<input type="password" className="pass-input" onChange={handleInputs} placeholder="Password" required={true} name="pass" />
            	   </div>
            	</div>
            	<a href="#">Forgot Password?</a>
                  
            <input  type="submit" className="btn" id="Aditya"/>
            
            <Link to={"/register"}>
            <button  className="btn" >Register</button>
            </Link>
           </form>
        </div>




        <div class="img">
			<img src="bg.svg"/>
      
      {info.arrdata.map((post , pos)=>{

      return(

      <div>



      <b> Hello{post.email} </b>

      <div>
        {post.card.map((sub , so)=>{

          return(


            <div className="card">
            <h5 className="card-header">{sub.prr}</h5>
            <div className="card-body">
                
      
                      
                </div>
          </div>
                  
      
      
      


          )

        })}

      </div>



      </div>
      )
      })
      }

		</div>



    </div>
    



    </>
    
    );
}
export default Login;
