import {  Link } from "react-router-dom";

import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";

import "../styles/Front.css";
import axios from "axios";
import React , {useEffect, useState} from "react";
import { useParams } from "react-router-dom";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";
function Front(){
  const { id } = useParams();
        
  const [info , setInfo]=useState({
    arr:"" , prr:"" , arrdata :[]
  });

  const [user , setUser] = useState({
    email : "" , pass :"" ,sir_name:"" ,product:""
   });



   
  
  function UpdateData(e){
        e.preventDefault();
        axios.put("/update/"+id , user)
        .then(data=>{
          console.log(data)          
        }).catch((err)=>{
            console.log(err);
          })
      }

      function handlechange(e){
        setInfo({
          ...info , [e.target.name] : e.target.value }
        )}



      function handleinputs(e){
        setUser({...user , [e.target.name] : e.target.value });
      };
  

      

    function PostData(e){
      e.preventDefault();

      axios.put("/entry/"+id , info)
      .then(data=>{
        console.log(data)
      
      }).catch((err)=>{
          console.log(err);
        })
  }  


      

  function Loaddata(){
    axios.get("/get-specific/"+id )
    .then((res)=> {
       console.log(res);
      setInfo({
        arrdata : res.data.card
      })
    })
    .catch((err)=>{
        console.log(err); 
      })
}

    useEffect(()=>{
      Loaddata();
    }, []
  )



    return(
        <>
        
            
    <div className="container">
      
      
          <form method="PUT" onSubmit={(e)=>UpdateData(e)}>
            <div className="centered">Welcome {user.sir_name}</div>
            <input type="text" onChange={(e)=>{handleinputs(e)} } name="sir_name"   placeholder="enter sirname "></input>
            
            <input type="submit" value="click me "></input>


        </form>
        <form method="PUT" onSubmit={(e)=>PostData(e)}>
            <input type="text" onChange={(e)=>{handlechange(e)}} name="arr1" placeholder="Enter post name "></input>
            <input type="submit" value="ccc "></input>
            </form>
            
           
    </div>

    

      { info.arrdata.map((post , pos)=>{

        return(
          <>

            <div className="card">
              <h5 className="card-header">{post.id}</h5>
              <div className="card-body">
                  <div>
                      <Link to={"/edit/"}>
                        <button  className="btn btn-primary">Edit</button>
                      </Link>
                  </div>

                        
                
                <a href="" className="btn btn-primary">delete</a>
              </div>
            </div>
                    



          </>


        )

      })
      }


    
    
  
        </>
        
    )

}
export default Front;
