import {  Link , useHistory } from "react-router-dom";
import '../styles/Login.css';
import React , {useEffect, useState } from "react";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";


function Login(){

  const [info , setInfo]=useState({
    arr:"" , prr:"" , arrdata :[]
  });

  
  function Loaddata(){
    axios.get("/getdata" )
    .then((res)=> {
       console.log(res);
      setInfo({
        arrdata : res.data.card
      })
    })
    .catch((err)=>{
        console.log(err); 
      })
}

    useEffect(()=>{
      Loaddata();
    }, []
  )



  const history = useHistory();
  const [user , setUser] = useState({
    arr:"" , prr:"" , email : "" , pass :"" , info:[]
 
   });

   function Loaddata(){
     axios.get("/getdata")
     .then((response)=>{
        console.log(response);
        setUser({
        info : response.data
        })
  
      })
     .catch((err)=>{
       console.log(err);
     })
   }

    useEffect(()=>{
      Loaddata();}, []
    )





 
   let name,value ;
   
 const handleInputs = (e) => {
   console.log(e);
   name=e.target.name;
   value=e.target.value;
 
   setUser({ ...user , [name]:value});
 }
 
 
 
 function PostData(e){
   e.preventDefault();
   
   
  axios.post("/login" , user)
   .then(function(response){
 
     var adi = response.data;
     console.log(adi._id)
     history.push("/main/"+adi._id);
     window.alert(response);
   
   
   }).catch((err)=>{
     console.log(err);
   })
   
 }
 
 

 return(
        <>


    <div className="samarth" >
     <form method="post" onSubmit={(e)=>PostData(e)}>
        
        <input type="email" onChange={handleInputs} required="true" placeholder="enter your email" name="email" />
        <input type="password" onChange={handleInputs} required="true" placeholder="enter your pass" name="pass" />
      
        <input  type="submit" className="btn" id="Aditya"/>
        <Link to={"/register"}>
        <button  id="BUT">Register</button>
        </Link>
      </form>
    </div>
    
    <div id="sam">
    
  { user.info.map((post , pos )=>{
    return(
       <> 
      <div> <b  id="BUT">{post.prr} wants to buy {post.name}</b></div>
      
      
      </>
    )
    })
}
    
    
    </div>







    </>
    
    );
}
export default Login;
