import {  Link } from "react-router-dom";
import '../styles/Login.css';
import React , {useEffect, useState} from "react";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";


function Login(){


      
  const [user , setUser] = useState({
    email : "" , pass :"" , info:[]
 
   });

   function Loaddata(){
     axios.get("/getdata")
     .then((response)=>{
        console.log(response);
        setUser({
        info : response.data
        })
      })
     .catch((err)=>{
       console.log(err);
     })
   }

    useEffect(()=>{
      Loaddata();}, []
    )





 
   let name,value ;
   
 const handleInputs = (e) => {
   console.log(e);
   name=e.target.name;
   value=e.target.value;
 
   setUser({ ...user , [name]:value});
 }
 
 
 
 function PostData(e){
   e.preventDefault();
   
   
  axios.post("/login" , user)
   .then(function(response){
 
     console.log(response);
     window.alert(response);
   
   
   }).catch((err)=>{
     console.log(err);
   })
 
 }
 
 

 return(
        <>


    <div className="samarth" >
     <form method="post" onSubmit={(e)=>PostData(e)}>
        
        <input type="email" onChange={handleInputs} placeholder="enter your email" name="email" />
        <input type="password" onChange={handleInputs} placeholder="enter your pass" name="pass" />
      
        <input  type="submit" className="btn" id="Aditya"/>
        <Link to={"/register"}>
        <button  id="BUT">Register</button>
        </Link>
      </form>
    </div>
    
    <div id="sam">
    
    
    
    
    
    </div>







    </>
    
    );
}
export default Login;
