import {  Link , useHistory } from "react-router-dom";
import '../styles/Login.css';
import React , {useEffect, useState } from "react";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";
require ("../pictures/bg.svg");
require("../pictures/avatar.svg");
require("../pictures/wave.png");

function Login(){


  const history = useHistory();
  const [user , setUser] = useState({
    arr:"" , prr:"" , email : "" , pass :"" , data:[]
 
   });

   function Loaddata(){
     axios.get("/getdata")
     .then((response)=>{
        console.log(response);
        setUser({
        data : response.data
        })
  
      })
     .catch((err)=>{
       console.log(err);
     })
   }

    useEffect(()=>{
      Loaddata();
    }, []
    )


 
   let name,value ;
   
 const handleInputs = (e) => {
   console.log(e);
   name=e.target.name;
   value=e.target.value;
 
   setUser({ ...user , [name]:value});
 }
 
 
 
 function PostData(e){
   e.preventDefault();
   
   
  axios.post("/login" , user)
   .then(function(response){
 
     var adi = response.data;
     console.log(adi._id)
     history.push("/main/"+adi._id);
     window.alert(response);
   
   
   }).catch((err)=>{
     console.log(err);
   })
   
 }
 

 return(
        <>




	<nav class="navbar-inverse navbar-fixed-top" role="navigation">    
		<div class="container-fluid">      

			<div class="navbar-header">        
				<button type="button" class="navbar-toggle collapsed" data-toggle="collapse" data-target="#bs-example-navbar-collapse-1">    
					<span class="sr-only">Toggle navigation</span>          
					<span class="icon-bar"></span>          
					<span class="icon-bar"></span>          
					<span class="icon-bar"></span>        
				</button>       
				<a class="navbar-brand" href="ecommerce.html" target="container">Store</a>      
			</div>

        	      
            <div class="collapse navbar-collapse" id="bs-example-navbar-collapse-1">
            	<ul class="nav navbar-nav">          
            		<li class="dropdown">            
            			<a href="#"  class="active dropdown-toggle" data-toggle="dropdown">Categories <span class="caret"></span></a>
            			<ul class="dropdown-menu" role="menu">              
            				<li><a href="category.html">Apparel  &amp; Accessories</a></li>              
            				<li><a href="category.html">Baby Products</a></li>              
            				<li><a href="category.html">Beauty  &amp; Health</a></li>             
            		    	<li><a href="category.html">Electronics</a></li>              
            		    	<li><a href="category.html">Furniture</a></li>              
            		    	<li><a href="category.html">Home  &amp; Garden</a></li>              
            		    	<li><a href="category.html">Luggage  &amp; Bags</a></li>              
            		    	<li><a href="category.html">Shoes</a></li>              
            		    	<li><a href="category.html">Sports  &amp; Entertainment</a></li>              
            		    	<li><a href="category.html">Watches</a></li>              
            		    	<li class="divider"></li>              
            		    	<li><a href="ecommerce.html">All Categories</a></li>            
            			</ul>          
            		</li>          
            		<li><a href="#">Link</a></li>          
            		<li><a href="#">Link</a></li>        
            	</ul>
            	<ul class="nav navbar-nav navbar-right">          
            		<li><a href="#"><span class="badge pull-right">4</span><i class="glyphicon glyphicon-shopping-cart"></i></a></li>      
            		<li><a href="account.html"><i class="glyphicon glyphicon-user"></i></a></li>          
            		<li><a href="#" data-toggle="modal" data-target="#myModal">Sign in</a></li>        
            	</ul>       
        	</div> 
        </div>
    </nav>
	

























	<img class="wave" src="wave.png"/>
	<div class="container">

		<div class="login-content">
    <form method="post" onSubmit={(e)=>PostData(e)}>
				<img src="avatar.svg"/>
				<h2 class="title">Welcome</h2>
           		<div class="input-div one">
           		   <div class="i">
           		   		<i class="fas fa-user"></i>
           		   </div>
           		   <div class="div">
           		   		
           		   		<input type="email" class="emaill" onChange={handleInputs} placeholder="Username" required={true} name="email" />
           		   </div>
           		</div>
           		<div class="input-div pass">
           		   <div class="i"> 
           		    	<i class="fas fa-lock"></i>
           		   </div>
           		   <div class="div">
           		    	
           		    	<input type="password" class="pass-input" onChange={handleInputs} placeholder="Password" required={true} name="pass" />
            	   </div>
            	</div>
              <div>
            	  <a href="#">Forgot Password?</a>
              </div>
              <div>
            <input  type="submit" className="btn btn-primary" id="Aditya"/>
            </div>
            <Link to={"/register"}>
            <button  class="btn btn-primary" >Register</button>
            </Link>
           </form>
        </div>




        <div class="img">
		    	<img src="bg.svg"/>
    		</div>



    </div>
    



    </>
    
    );
}
export default Login;
