import {  Link , useHistory } from "react-router-dom";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";

import "../styles/Post.css";
import axios from "axios";
import React , {useEffect, useState} from "react";
import { useParams } from "react-router-dom";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";


function Post(){
  const { id } = useParams();
  const history = useHistory();      
  const [info , setInfo]=useState({
    description:"" , task:"" , arrdata :[]
  });
;



   
  
  
      function handlechange(e){
        setInfo({
          ...info , [e.target.name] : e.target.value }
        )}




      

    function PostData(e){
      e.preventDefault();

      axios.put("/entry/"+id , info)
      .then(data=>{
        console.log(data)
        history.push("/main/"+id)
      
      }).catch((err)=>{
          console.log(err);
        })
  }  



    return(
        <>





        
	<nav class="navbar navbar-expand-lg navbar-light fixed-top">
		<div class="container">
			<a class="navbar-brand" href="#"><b> Welcome</b></a> <button aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation" class="navbar-toggler" data-target="#navbarSupportedContent" data-toggle="collapse" type="button"><span class="navbar-toggler-icon"></span></button>
			<div class="collapse navbar-collapse" id="navbarSupportedContent">
                
				<ul class="navbar-nav ml-auto">
					<li class="nav-item active">
						<a class="nav-link" href="#">
                            <Link to={"/main/"+id}>
                                <button  className="btn btn-primary">Home</button>
                            </Link>
                        </a>
					</li>
					<li class="nav-item">
						<a class="nav-link" href="#">
                          
                                 <button  className="header btn btn-primary"> Create Post</button>
                                 
                        </a>
					</li>
					<li class="nav-item">
						<a class="nav-link" href="#">
                            <Link to={"/AllPost/"+id}>
                                <button  className="header btn btn-primary">View My Products</button>
                            </Link>
                        </a>
					</li>
					
                    <li>
                    <Link to={"/User_Profile/"+id}>
                            <button  className="btn btn-primary" id="update-but">Update Profile</button>
                    </Link>
            
                    </li>
				</ul>
			</div>

      
		</div>
	</nav>

        
            
    <div className="cont">
      
      
         

         <form method="PUT" onSubmit={(e)=>PostData(e)}>
            <input type="text" onChange={(e)=>{handlechange(e)}} name="task" required={true} placeholder="Enter any Task "></input>
           <br />
            <textarea type="text" onChange={(e)=>{handlechange(e)}} name="description" required={true} placeholder="Enter short description about Task "></textarea>
           <br/>
           <br/>
           

            <input type="submit" className="btn btn-primary"></input>
         </form>
       
            
           
    </div>

    


    
    
  
        </>
        
    )

}
export default Post;
