import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import img from "../pictures/234403.jpg"
//import "../styles/Post.css";
import axios from "axios";
import React , {useEffect, useState} from "react";
import { useParams } from "react-router-dom";

function Post(){
  const { id } = useParams();
        
  const [user , setUser] = useState({
    sir_name:"" 
 
   });

   function Loaddata(){
    axios.get("/get-specific/"+id )
    .then((res)=> {
       console.log(res);
      setUser(res.data)
    })
    .catch((err)=>{
        console.log(err); 
      })
}


    useEffect(()=>{
      Loaddata();
    }, []
  )

    function UpdateData(e){
        e.preventDefault();
        axios.put("/update/"+id , user)
        .then(data=>{
          console.log(data)

          
        }).catch((err)=>{
            console.log(err);
          })
      }



      function handleinputs(e){
        setUser({
            
            sir_name:e.user.sir_name ,


        });
      };
  
      


    return(
        <>
        
            
    <div className="container">
        <img src={img} className="rounded" alt="..."/>
      
          <form method="PUT" onSubmit={(e)=>UpdateData(e)}>
            <div className="centered">Welcome {user.sir_name}</div>
            <input type="text" onChange={(e)=>{handleinputs(e)} } name="sir_name"  value={user.sir_name} placeholder="enter sirname "></input>
      
        
            <input type="submit" value="click me "></input>


        </form>
        
           
    </div>
    
  
        </>
        
    )

}
export default Post;
