import {  Link , useParams } from "react-router-dom";

import React , {useEffect, useState } from "react";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";



function AllPost(){

    const {id} = useParams();
  
         
  const [info , setInfo]=useState({
    arr:"" , prr:"" , arrdata :[]
  });


          

function Loaddata(){
    axios.get("/get-specific/"+id )
    .then((res)=> {
       console.log(res);
      setInfo({
        arrdata : res.data.card
      })
    })
    .catch((err)=>{
        console.log(err); 
      })
}

    useEffect(()=>{
      Loaddata();
    }, []
  )



  function deletepost(id , post_id){

    axios.delete("/delete/"+id+"/"+post_id)
    .then(data=>{
      console.log(data)
    
    }).catch((err)=>{
        console.log(err);
      })


  }




    return(
        <>
        <div>
          <Link to={"/AllPost/"+id}>
          <button  className="btn">View My solded products</button>
          </Link>
        </div>
        
        <div>
          <Link to={"/AllPost/"+id}>
          <button  className="btn">View I getted products</button>
          </Link>
        </div>

      {info.arrdata.map((post , pos)=>{

return(
  

    <div className="card">
      <h5 className="card-header">{post.prr}</h5>
      <div className="card-body">
          

                
          <button onClick={()=>{deletepost( id , post._id)}} className="btn btn-primary">Delete</button>
      </div>
    </div>
            



  


)

})
}

        </>


    )

}
export default AllPost;
