import {  Link , useParams } from "react-router-dom";
import "../styles/AllPost.css"
import React , {useEffect, useState } from "react";
import "../../node_modules/bootstrap/dist/css/bootstrap.min.css";
import axios from "axios";



function All_Quiz(){
  const {id} = useParams();

  const [info , setInfo]=useState({
     Title_Quiz:"" , arrdata :[]
  });

   
  const [user , setUser]=useState({
    email:"" , pass:"" , name:""
});

  
  function Load(){
    
    axios.get("/get_Quiz/"+id)
    .then((res)=> {
       console.log(res);
      setInfo({
        arrdata : res.data
      })
    })
    .catch((err)=>{
        console.log(err); 
      })



      axios.get("/get_specific_admin/"+id)

        .then((res)=>{
            console.log(res);
            setUser(res.data);

        })
        .catch((err)=>{
            console.log(err);
        })
}

    useEffect(()=>{
      Load();
    }, []
  )


  

    function deletequiz(post_id , Quiz_id){

    axios.put("/update/"+post_id+"/"+Quiz_id)
    .then(data=>{
      console.log(data)
    
    }).catch((err)=>{
        console.log(err);
      })


  }



    return(
        <>

        
	<nav class="navbar navbar-expand-lg navbar-light fixed-top">
		<div class="container">
   
			<a class="navbar-brand" href="#"><b> Welcome {user.name} </b></a> <button aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation" class="navbar-toggler" data-target="#navbarSupportedContent" data-toggle="collapse" type="button"><span class="navbar-toggler-icon"></span></button>
			<div class="collapse navbar-collapse" id="navbarSupportedContent">
                
				<ul class="navbar-nav ml-auto">
					<li class="nav-item active">
						<a class="nav-link" href="#">
                        <Link to={"/mainpage_admin/"+id}>
                                <button  className="btn btn-primary">Home</button>
                        </Link>
                        </a>
					</li>
					<li class="nav-item">
						<a class="nav-link" href="#">
                            <Link to={"/create_quiz/"+id}>
                                 <button  className="header btn btn-primary"> Create Quiz</button>
                            </Link>            
                        </a>
					</li>
					          <li>
                    <Link to={"/User_Profile/"+id}>
                            <button  className="btn btn-primary" id="update-but">Update Profile</button>
                    </Link>
            
                    </li>
				</ul>
			</div>
		</div>
	</nav>



      <div className="table">

  <table>
  <tr>
    <th> 
      
          <b>My Current Quizes To Do</b>
      
    </th>
    
    <th>
      
          <b>My Deleted Quizes</b>
      
    </th>
  </tr>
  <tr>
    <td>
     
	<div class="carousel slide" data-ride="carousel" id="carouselExampleIndicators">

 
    
    {info.arrdata.map((post , pos)=>{
    return(
    <div>
        

        <div>
        {post.Quiz.map((sub , so)=>{
            return(
        <div className="card">
                    
                    
                    <b className="card-header">{post.name}</b>
                    
                    <p> Title of your quiz is</p>
                    <div className="card-body">
                        
                    <b className="card-header">{sub.Title_quiz}</b>
                    <button  className="btn btn-primary" id="del-but" onClick={()=>updatequiz(post._id , sub._id)}>Delete</button>
                    </div>
                    
                                
        </div>

            )

        })}
        </div>

    </div>
    )
    })
    }



</div> 
    </td>
    <td>     
      
           
	<div class="carousel slide" data-ride="carousel" id="carouselExampleIndicators">

 
    
    {info.arrdata.map((post , pos)=>{
    return(
    <div>
        

        <div>
        {post.Del_Quiz.map((sub , so)=>{
            return(
        <div className="card">
                    
                    
                    <b className="card-header">{post.name}</b>
                    
                    <p> Title of your quiz is</p>
                    <div className="card-body">
                        
                    <b className="card-header">{sub.Title_quiz}</b>
                    
                    </div>
                    
                                
        </div>

            )

        })}
        </div>

    </div>
    )
    })
    }



</div> 

</td>
 </tr>
</table>
</div>
        </>


    )

}
export default All_Quiz;
