import React , {useEffect, useState} from "react";
import {  Link , useParams , useHistory} from "react-router-dom";
import axios from "axios";
import "../styles/Actual_Quiz.css";

function Actual_Quiz(){

        const {id} = useParams();
        const {Quiz_id} = useParams();
        const {user_id} = useParams();

       const history = useHistory();

  const [info , setInfo]=useState({
    Title_Quiz:"" , selected_option:"" ,que:"" , option1:"", option2:"" , option3:"" , option4:"", ans:""  , arrdata :[]
  });

  const [data , setData]=useState({
    Title_quiz:"" , selected_option:"" ,que:"" , option1:"", option2:"" , option3:"" , option4:"", ans:"" ,  arrdata :[]
 });
 const [score , setScore]=useState({
  Score_Quiz:"" , arrdata:[]
});


 const onC = (event) =>{

  
  console.log(`option_selected  : ${event.target.getAttribute("dataid").split("-")[1]}`);
  console.log(`Question_id  : ${event.target.getAttribute("dataid").split("-")[0]}`);
  console.log(`Title  : ${event.target.getAttribute("dataid").split("-")[2]}`);
  
  
  const name = "selected_option"
  const value = event.target.getAttribute("dataid").split("-")[1]


  
  const que = "que"
  const que_value = event.target.getAttribute("dataid").split("-")[0]

  const title = "Title_quiz"
  const title_value = event.target.getAttribute("dataid").split("-")[2]

  console.log(value);

  setData({
    ...data , [name] : value ,   [que] : que_value , [title] : title_value

  })
 

 }


 function Load(){
    
  axios.get("/get-specific-quiz/"+id+"/"+Quiz_id)
  .then((res)=> {
     console.log(res);
    setInfo({
      arrdata : res.data.Quiz
    })

  })
  .catch((err)=>{
      console.log(err); 
    })
  }

  
  useEffect(()=>{
    Load();
  }, []
)



function UpdateData(){

  axios.put("/sub_que/"+user_id+"/"+id+"/"+Quiz_id, data)

  .then((res)=>{
      console.log(res);
     
  })
  .catch((err)=>{
      console.log(err);
  })
}


function Score_Quiz(event){

  
  
  const aa = event.target.getAttribute("dataid").split("-")[0]

  history.push("/view_score"+"/"+user_id+"/"+aa)
  
}



return(



  <>
  
  <div>



        <nav class="navbar navbar-expand-lg navbar-light fixed-top">
		<div class="container">
   
			<a class="navbar-brand" href="#"><b> Welcome {user.name} </b></a> <button aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation" class="navbar-toggler" data-target="#navbarSupportedContent" data-toggle="collapse" type="button"><span class="navbar-toggler-icon"></span></button>
			<div class="collapse navbar-collapse" id="navbarSupportedContent">
                
				<ul class="navbar-nav ml-auto">
					<li class="nav-item active">
						<a class="nav-link" href="#">
                        <Link to={"/mainpage_user/"+id}>
                                <button  className="btn btn-primary">Home</button>
                        </Link>
                        </a>
					</li>
					<li class="nav-item">
                    <a class="nav-link" href="#">
                            <Link to={"/user_quiz_history/"+id}>
                                <button  className="header btn btn-primary">View My Quizs</button>
                            </Link>
                        </a>
					</li>
					          <li>
                    <Link to={"/User_Profile/"+id}>
                            <button  className="btn btn-primary" id="update-but">Update Profile</button>
                    </Link>
            
                    </li>
				</ul>
			</div>
		</div>
	</nav>
<div className="Content">
<div class="card border-dark mb-3" >

{info.arrdata.map((post , pos)=>{
return(
<div>
  
  
<div class="card-header"> <b>Title Of Quiz is : </b>{post.Title_quiz}</div>


  {post.Questions.map((sub)=>{
      return(
      <div key={sub._id}>

       
     
      <div class="card border-dark mb-3 question">
      <div class="card-header"><b>Question : </b>{sub.que}</div>

      <div class="card-body text-dark">
       
          <form>
              <p class="card-text"><input type="radio" name="selected_option" value={sub.option1} onClick={onC} dataid = {`${sub.que}-${sub.option1}-${post.Title_quiz}`} key={`${sub.que}-${sub.option1}-${post.Title_quiz}`}  />(A) : <strong>{sub.option1}</strong></p>
              <p class="card-text"><input type="radio" name="selected_option" value={sub.option2} onClick={onC} dataid = {`${sub.que}-${sub.option2}-${post.Title_quiz}`} key={`${sub.que}-${sub.option2}-${post.Title_quiz}`} />(B) : <strong>{sub.option2}</strong></p>
              <p class="card-text"><input type="radio" name="selected_option" value={sub.option3} onClick={onC} dataid = {`${sub.que}-${sub.option3}-${post.Title_quiz}`} key={`${sub.que}-${sub.option3}-${post.Title_quiz}`} />(C) : <strong>{sub.option3}</strong></p>
              <p class="card-text"><input type="radio" name="selected_option" value={sub.option4} onClick={onC} dataid = {`${sub.que}-${sub.option4}-${post.Title_quiz}`} key={`${sub.que}-${sub.option4}-${post.Title_quiz}`} />(D) : <strong>{sub.option4}</strong></p>
          </form>
          
          
              <br/>
          <b>Option : {data.selected_option}</b>
          
          <br/>
          <button  onClick={()=>{UpdateData()}} className="btn btn-primary">Submit</button>
          <h5 class="card-title"><b>Answer : </b>{sub.ans}</h5>
      </div>
      </div>
      
              
       </div>

      )
 
 })}
 

 <div class="class-footer">

  <button onClick={Score_Quiz} dataid = {`${post.Title_quiz}`} key={`${post.Title_quiz}`} className="btn btn-primary">Submit</button>

</div>
</div>

)
})}


</div> 
</div>
</div>


  </>
)

}
export default Actual_Quiz;
