import React , {useEffect, useState} from "react";
import {  Link , useParams } from "react-router-dom";
import axios from "axios";
import "../styles/Quiz.css";

function Quiz(){

    const {id} = useParams();

  const [info , setInfo]=useState({
     Title_Quiz:"" , arrdata :[]
  });

   
  const [user , setUser]=useState({
    email:"" , pass:"" , name:""
});

  
  function Load(){
    
    axios.get("/get_Quiz/"+id)
    .then((res)=> {
       console.log(res);
      setInfo({
        arrdata : res.data
      })
    })
    .catch((err)=>{
        console.log(err); 
      })



      axios.get("/get_specific_user/"+id)

        .then((res)=>{
            console.log(res);
            setUser(res.data);

        })
        .catch((err)=>{
            console.log(err);
        })
}

    useEffect(()=>{
      Load();
    }, []
  )


   
    



   

    function UpdateData(user_id , post_id ,id){
          

        axios.put("/update/"+user_id+"/"+post_id+"/"+id, info)

        .then((res)=>{
            console.log(res);
           
        })
        .catch((err)=>{
            console.log(err);
        })
    }




    return(



        <>
        
        <div>
      


        <nav class="navbar navbar-expand-lg navbar-light fixed-top">
		<div class="container">
		 <b> Welcome {user.email}</b> 
	   <div class="collapse navbar-collapse" id="navbarSupportedContent">
                
				<ul class="navbar-nav ml-auto">
					
					  <li class="nav-item">					
              <Link to={"/take_quiz/"+id}>
                <button  className="header btn btn-primary"> Take Quiz</button>
              </Link>                      
					  </li>
      		  <li>		
              <Link to={"/user_quiz_history/"+id}>
                 <button  className="header btn btn-primary">View My Quizs</button>
              </Link>
      			</li>
		
             <li>
               <Link to={"/User_Profile/"+id}>
                  <button  className="btn btn-primary" id="update-but">Update Profile</button>
               </Link>
             </li>
             
             <li class="nav-item">
                <Link to={"/userlogin"}>
                   <button  className="header btn btn-primary"> LogOut</button>
                </Link>            
		     </li>

				</ul>
			</div>
		</div>
	</nav>

	<div class="carousel slide actual" data-ride="carousel" id="carouselExampleIndicators">

 
    
    {info.arrdata.map((post , pos)=>{
    return(
    <div>
        

        <div>
        {post.Quiz.map((sub , so)=>{
            return(
        <div className="card">
                    
                    
                    <h6 className="card-header"><p> Title of quiz <b> {sub.Title_quiz}</b></p></h6>

                  
                                     
                    <div className="card-body">
                        
                    <p>description About Quiz : <h5>{sub.Desc_quiz}</h5></p>
                    <Link to={"/actual_quiz/"+post._id+"/"+sub._id +"/"+id}>
                            <button  className="btn btn-primary" id="update-but">Start</button>
                    </Link>

                    
                    </div>
                    
                                
        </div>

            )

        })}
        </div>

    </div>
    )
    })
    }



</div> 
    </div>
      
        </>
    )

}
export default Quiz;
